import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-table',
  templateUrl: './table.component.html',
  styleUrls: ['./table.component.css']
})
export class TableComponent {

  @Input() heads:string[]=[];
}
<table>
  <thead>
    <tr>
      <th *ngFor="let item of heads">{{ item }}</th>
    </tr>
  </thead>

  <tbody>
    <tr>
      <ng-content #name></ng-content>
    </tr>
  </tbody>
</table>
