    // },
    // {
    //   path:"**",//garip bir url girildiğinde yönlendirilecek sayfa ayarı yapıldı.
    //   component:NotFoundComponent
    // }
    // ])
  ],
  providers: [],
  bootstrap: [AppComponent]
})
export class AppModule { }
