import { Component } from '@angular/core';

@Component({
  selector: 'app-c1',
  templateUrl: './c1.component.html',
  styleUrls: ['./c1.component.scss']
})
export class C1Component {

  heads:string[]=[
    "#",
    "alan 1 ",
    "alan 2 ",
    "alan 3 ",

  ]
 list=[
  {
    id:"1",
    name:"umut",
    lastName:"atraş"
  }
 ]
}
<p>c1 works!</p>

<app-table [heads]="heads">
  <tr *ngFor="let item of list; let index = index">
    <td>{{ index + 1 }}</td>
    <td>{{ item.name }}</td>
    <td>{{ item.lastName }}</td>
  </tr>
</app-table>
