import { DatePipe } from '@angular/common';
import { Component } from '@angular/core';

@Component({
  selector: 'app-pipe',
  templateUrl: './pipe.component.html',
  styleUrls: ['./pipe.component.css']
})
export class PipeComponent {
  constructor(private _date:DatePipe){
    this.inputdate=this._date.transform(new Date(),'yyyy-MM-dd')

  }
  money:number=0;
  dateString:Date=new Date();
  inputdate:string="2023-11-07";
  searc:string="";
names:string[]=[
  "Ahmet",
  "Mehmet",
  "Umut",
  "Barış"
];

users:{
  name:String,
  age:number
}[]=[
{
  name:"umut",
  age:12
}
];
}
<p>pipe works!</p>

<h4>{{dateString | date:'dd.MM.yyyy'}}</h4>

<input type="date" [(ngModel)]="inputdate">

<input [(ngModel)]="searc" type="text">
<ul>
  <li *ngFor="let item of names | namePipe:searc">
      {{item}}
  </li>
</ul>


<input type="text" [(ngModel)]="money">
<h4>Para birimi {{money | currency:'TRY':'symbol-narrow':'1.2-2'}} </h4>
